// src/components/EnrollmentForm.js
import React, { useState } from 'react';

const EnrollmentForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        age: 0,
        selected_batch: '',
    });

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:8000/api/enroll/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });
            const data = await response.json();
            console.log(data);
            // Handle the payment response and display appropriate message to the user
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Include form fields for name, age, and batch selection */}
            <button type="submit">Enroll</button>
        </form>
    );
};

export default EnrollmentForm;
