// src/components/EnrollmentForm.js
import React, { useState } from 'react';

const EnrollmentForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        age: 18, // Set a default age value within the range
        selected_batch: '',
    });

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Validate age within the range
            if (formData.age < 18 || formData.age > 65) {
                console.error('Invalid age. Age must be between 18 and 65.');
                return;
            }

            // Validate batch selection
            if (!formData.selected_batch) {
                console.error('Please select a batch.');
                return;
            }

            const response = await fetch('http://localhost:8000/api/enroll/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                console.error('Enrollment failed. Please try again.');
                return;
            }

            const data = await response.json();
            console.log(data);
            // Handle the payment response and display appropriate message to the user
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Name:
                <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                />
            </label>
            <br />
            <label>
                Age:
                <input
                    type="number"
                    name="age"
                    value={formData.age}
                    onChange={handleInputChange}
                    min="18"
                    max="65"
                    required
                />
            </label>
            <br />
            <label>
                Select Batch:
                <select
                    name="selected_batch"
                    value={formData.selected_batch}
                    onChange={handleInputChange}
                    required
                >
                    <option value="">-- Select Batch --</option>
                    <option value="6-7AM">6-7AM</option>
                    <option value="7-8AM">7-8AM</option>
                    <option value="8-9AM">8-9AM</option>
                    <option value="5-6PM">5-6PM</option>
                </select>
            </label>
            <br />
            <button type="submit">Enroll</button>
        </form>
    );
};

export default EnrollmentForm;
