// src/components/EnrollmentForm.js
import React, { useState } from 'react';

const EnrollmentForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        age: 18,
        selected_batch: '',
    });

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate age within the range
        if (formData.age < 18 || formData.age > 65) {
            console.error('Invalid age. Age must be between 18 and 65.');
            return;
        }

        // Validate batch selection
        if (!formData.selected_batch) {
            console.error('Please select a batch.');
            return;
        }

        try {
            const response = await fetch('http://localhost:8000/api/enroll/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                console.error('Enrollment failed. Please try again.');
                return;
            }

            const data = await response.json();
            console.log(data);
            // Handle the payment response and display appropriate message to the user
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.header}>Yoga Class Enrollment Form</h2>
            <form onSubmit={handleSubmit} style={styles.form}>
                <label style={styles.label}>
                    Name:
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        required
                        style={styles.input}
                    />
                </label>
                <br />
                <label style={styles.label}>
                    Age:
                    <input
                        type="number"
                        name="age"
                        value={formData.age}
                        onChange={handleInputChange}
                        min="18"
                        max="65"
                        required
                        style={styles.input}
                    />
                </label>
                <br />
                <label style={styles.label}>
                    Select Batch:
                    <select
                        name="selected_batch"
                        value={formData.selected_batch}
                        onChange={handleInputChange}
                        required
                        style={styles.input}
                    >
                        <option value="">-- Select Batch --</option>
                        <option value="6-7AM">6-7AM</option>
                        <option value="7-8AM">7-8AM</option>
                        <option value="8-9AM">8-9AM</option>
                        <option value="5-6PM">5-6PM</option>
                    </select>
                </label>
                <br />
                <button type="submit" style={styles.button}>Enroll</button>
            </form>
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        marginTop: '50px',
    },
    header: {
        color: '#333',
    },
    form: {
        width: '300px',
        margin: 'auto',
        padding: '20px',
        boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
        borderRadius: '5px',
        backgroundColor: '#fff',
    },
    label: {
        display: 'block',
        margin: '10px 0',
        color: '#555',
    },
    input: {
        width: '100%',
        padding: '8px',
        boxSizing: 'border-box',
        border: '1px solid #ccc',
        borderRadius: '3px',
    },
    button: {
        backgroundColor: '#4CAF50',
        color: 'white',
        padding: '10px',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
    },
};

export default EnrollmentForm;
