// src/components/EnrollmentForm.js
import React, { useState } from 'react';
import './EnrollmentForm.css'; // Import a separate CSS file for styling

const EnrollmentForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        age: 18,
        selectedBatch: '',
    });
    const [formStatus, setFormStatus] = useState(null);

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleBatchChange = (e) => {
        setFormData({ ...formData, selectedBatch: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Validate age within the range
            if (formData.age < 18 || formData.age > 65) {
                setFormStatus('Invalid age. Age must be between 18 and 65.');
                return;
            }

            // Validate batch selection
            if (!formData.selectedBatch) {
                setFormStatus('Please select a batch.');
                return;
            }

            const response = await fetch('http://localhost:8000/api/enroll/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    ...formData,
                    selected_batch: formData.selectedBatch,
                }),
            });

            console.log('Response Status:', response.status);
            const data = await response.json();
            console.log('Response Data:', data);

            if (!response.ok) {
                setFormStatus('Enrollment failed. Please try again.');
                return;
            }

            setFormStatus('Form submitted successfully!');
            // Mock payment processing
            // const paymentResponse = await CompletePayment(formData, fee);
            // setPaymentStatus(paymentResponse.status);
            // setSummaryVisible(true);
        } catch (error) {
            console.error('Error during enrollment:', error);
            setFormStatus('An error occurred. Please try again later.');
        }
    };

    return (
        <div className="enrollment-form-container">
            <h2>Yoga Class Enrollment Form</h2>
            <form onSubmit={handleSubmit} className="enrollment-form">
                <label>
                    Name:
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <label>
                    Age:
                    <input
                        type="number"
                        name="age"
                        value={formData.age}
                        onChange={handleInputChange}
                        min="18"
                        max="65"
                        required
                    />
                </label>
                <label>
                    Select Batch:
                    <select
                        name="selectedBatch"
                        value={formData.selectedBatch}
                        onChange={handleBatchChange}
                        required
                    >
                        <option value="">-- Select Batch --</option>
                        <option value="6-7AM">6-7AM</option>
                        <option value="7-8AM">7-8AM</option>
                        <option value="8-9AM">8-9AM</option>
                        <option value="5-6PM">5-6PM</option>
                    </select>
                </label>
                <button type="submit" disabled={formStatus !== null}>
                    Enroll
                </button>
            </form>
            {formStatus && <div className="form-status">{formStatus}</div>}
        </div>
    );
};

export default EnrollmentForm;
